using System.Collections.Generic;namespace gLibraries{public class gCookie{public static IDictionary<string,dynamic>all(){IDictionary<string,dynamic>lista=Xamarin.Forms.Application.Current.Properties;return lista;}public static dynamic get(string name){return all()[name];}public static bool set(string name,dynamic value){Xamarin.Forms.Application.Current.Properties[name]=value;return true;}public static bool clean(dynamic e=null){List<string>keys=new List<string>();if(e is string){keys.Add(e);}else if(e is List<string>){keys=e;}else{keys=(List<string>)all().Keys;}keys.ForEach(key=>{Xamarin.Forms.Application.Current.Properties.Remove(key);});return true;}}}