class Cookies{static set(t,e,i=!1){let l="";if(i){let s=new Date;s.setTime(s.getTime()+864e5*i),l="; expires="+s.toUTCString()}document.cookie=t+"="+e+l+"; path=/"}static get(t){let e=document.cookie.split(";");for(let i=0;i<e.length;i++){let l=e[i].trim();if(l.startsWith(t+"="))return l.substring(t.length+1,l.length)}}static getAll(){let t=document.cookie.split(";"),e={};for(let i=0;i<t.length;i++){let l=t[i].trim(),s=l.split("=")[0];e[s]=l.substring(s.length+1,l.length)}return e}static delete(t){Cookies.set(t,"",-1)}static destroy(){let t=Cookies.getAll();for(let e in t)Cookies.delete(e)}}