<?php class gTrace{static private $mounths=['Enero','Febrero','Marzo','Abril','Mayo','Junio','Julio','Agosto','Septiembre','Octubre','Noviembre','Diciembre'];static public function getId():string{date_default_timezone_set('America/Lima');return date('YmdHisu');}static public function getDate(string $format='iso'):string{date_default_timezone_set('America/Lima');setlocale(LC_TIME,'es_ES.utf8');switch($format){case 'date':return date('Y-m-d');break;case 'time':return date('H:i:s');break;case 'mysql':return date('Y-m-d H:i:s');break;case 'iso':return date('Y-m-d\TH:i:s\Z');break;case 'gpt':return date('l, F d Y H:i:s');break;default:return date('Y-m-d H:i:s.u');break;}}static public function format(string $format):string{return date($format);}static public function month(string $index=''):string{if($index!=''){return gTrace::$mounths[intval($index)];}return gTrace::$mounths[date('n')-1];}}